package conditionalStatements;

public class if_else3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=145212;
		if(num%2==0)
		{
			System.out.println("Given number is even");
		}
		else
		{
			System.out.println("Given number is odd");
		}

	}

}
